CREATE TABLE `tema` (
  `id_tema` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `nombre_tema` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `desc_tema` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `src_tema` varchar(25) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `url_tema` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `visitas_tema` bigint(20) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id_tema`),
  UNIQUE KEY `uk_nombre_tema` (`nombre_tema`),
  KEY `k_id_tema` (`id_tema`)
) ENGINE=MyISAM AUTO_INCREMENT=65 DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
