DROP TABLE IF EXISTS `utilidad`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `utilidad` (
  `id_utilidad` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `id_apartado` int(10) unsigned DEFAULT NULL,
  `id_pertenencia_utilidad` int(10) unsigned NOT NULL DEFAULT '0',
  `id_sistema_operativo` int(10) unsigned DEFAULT NULL,
  `id_idioma` int(10) unsigned NOT NULL DEFAULT '0',
  `id_formato` int(10) unsigned NOT NULL DEFAULT '0',
  `id_licencia` int(10) unsigned DEFAULT NULL,
  `id_tipo_utilidad` int(10) unsigned NOT NULL DEFAULT '0',
  `id_persona` int(10) unsigned NOT NULL DEFAULT '0',
  `id_empresa` int(10) unsigned DEFAULT NULL,
  `nombre_utilidad` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `desc_utilidad` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `version_utilidad` varchar(10) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `url_utilidad` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `tamanio_utilidad` int(10) unsigned DEFAULT NULL,
  `visitas_utilidad` bigint(20) unsigned NOT NULL DEFAULT '0',
